/* eslint-disable max-classes-per-file */
const canvas = document.getElementById('canvas')! as HTMLCanvasElement;
const context: CanvasRenderingContext2D = canvas.getContext(
  '2d'
)! as CanvasRenderingContext2D;

const input_resolution = document.getElementById('resolution')! as HTMLElement;

const image = new Image();
image.src =
  'data:image/jpeg;base64,';

class Cell {
  x: number;

  y: number;

  symbol: string;

  color: string;

  constructor(x: number, y: number, symbol: string, color: string) {
    this.x = x;
    this.y = y;
    this.symbol = symbol;
    this.color = color;
  }

  draw(ctx: CanvasRenderingContext2D) {
    console.log(this.color);
    ctx.fillStyle = this.color;
    ctx.fillText(this.symbol, this.x, this.y);
  }
}

class AsciiEffect {
  private imageCellArray: Cell[] = [];

  private pixels: ImageData;

  private ctx: CanvasRenderingContext2D;

  private width: number;

  private height: number;

  constructor(ctx: CanvasRenderingContext2D, width: number, height: number) {
    this.ctx = ctx;
    this.width = width;
    this.height = height;
    this.ctx.drawImage(image, 0, 0, this.width, this.height);
    this.pixels = this.ctx.getImageData(0, 0, this.width, this.height);
  }

  private convertColorToSymbol(colorAvg: number) {
    if (colorAvg > 250) return '@';
    if (colorAvg > 240) return '!';
    if (colorAvg > 220) return '*';
    if (colorAvg > 200) return '+';
    if (colorAvg > 180) return '#';
    if (colorAvg > 160) return '&';
    if (colorAvg > 140) return '%';
    if (colorAvg > 120) return '_';
    if (colorAvg > 100) return ':';
    if (colorAvg > 80) return '$';
    if (colorAvg > 60) return '/';
    if (colorAvg > 40) return '-';
    if (colorAvg > 20) return 'x';
    if (colorAvg > 0) return 'w';
    return ' ';
  }

  private scanImage(cellSize: number) {
    this.imageCellArray = [];
    for (let i = 0; i < this.pixels.height; i += cellSize) {
      for (let j = 0; j < this.pixels.width; j += cellSize) {
        const posX: number = j * 4;
        const posY: number = i * 4;
        const pos = posY * this.pixels.width + posX;
        if (this.pixels.data[pos + 3] > 128) {
          const red: number = this.pixels.data[pos];
          const green: number = this.pixels.data[pos + 1];
          const blue: number = this.pixels.data[pos + 2];
          const averageColorvalue: number = (red + green + blue) / 3;
          const color = `rgb(${red},${green},${blue})`;
          const converToSymbol = this.convertColorToSymbol(averageColorvalue);
          if (averageColorvalue > 100)
            this.imageCellArray.push(new Cell(j, i, converToSymbol, color));
        }
      }
    }
    console.log(this.imageCellArray);
  }

  private drawAsAcii() {
    this.ctx.fillRect(0, 0, this.width, this.height);
    for (let i = 0; i < this.imageCellArray.length; i += 1) {
      this.imageCellArray[i].draw(this.ctx);
    }
  }

  draw(cellSize: number) {
    this.scanImage(cellSize);
    this.drawAsAcii();
  }
}

let effect: AsciiEffect;

image.onload = function initalize() {
  canvas.width = image.width;
  canvas.height = image.height;
  effect = new AsciiEffect(context, image.width, image.height);
  effect.draw(resolution);
};
let resolution = 5;
input_resolution?.addEventListener('change', (e: Event) => {
  const { target } = e;
  resolution = Number(target?.value);
  image.onload();
});
